import * as React from "react";
import { GoogleMap, Marker, withGoogleMap } from "react-google-maps";
import Polyline from "react-google-maps/lib/components/Polyline";
//import logo from "./MBLogo.png";

/*global google*/
/*const icon={{
  url: logo,
  scaledSize: new google.maps.Size(31, 43)
}}*/

const MBGoogleMap = withGoogleMap(props => (
  <GoogleMap
    defaultZoom={8}
    zoom={props.zoom}
    defaultCenter={{ lat: props.lat, lng: props.lng }}
    center={{ lat: props.lat, lng: props.lng }}
  >
    {props.isMarkerShown && (
      <Marker position={{ lat: props.lat, lng: props.lng }} />
    )}

    {props.polylines.map((polyline, index) => (
      <Polyline
        key={index}
        path={polyline}
        //geodesic={true}
        options={{
          strokeColor: "#008b45",
          strokeOpacity: 0.75,
          strokeWeight: 3
        }}
      />
    ))}

    {props.leadsGeoMarkers.map((geoMarkInfo, index) => (
      <Polyline />
    ))}
  </GoogleMap>
));

export default MBGoogleMap;
